package de.tum.nihaoshijie.flightsystem.client.controller;import com.jfoenix.controls.JFXButton;import javafx.event.ActionEvent;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.scene.layout.AnchorPane;import java.io.IOException;import java.util.Objects;public class ServiceMainController {    private static final String HOME_SCENE_PATH = "/clientview/fxmls/HomeScene.fxml";    private static final String ENTERTAINMENT_PATH = "/clientview/fxmls/EntertainmentScene.fxml";    private static final String INSTRUCTION_PATH = "/clientview/fxmls/InstructionScene.fxml";    private static final String SERVICE_PATH = "/clientview/fxmls/ServiceScene.fxml";    @FXML    private JFXButton backButton;    @FXML    private JFXButton entertainment;    @FXML    private JFXButton inFlightSystem;    @FXML    private JFXButton instruction;    @FXML    private AnchorPane mainPane;    @FXML    void changeToEntertainment(ActionEvent event) throws IOException {        mainPane.getScene().setRoot(FXMLLoader.load(Objects.requireNonNull(getClass().getResource(ENTERTAINMENT_PATH))));    }    @FXML    void changeToInstruction(ActionEvent event) throws IOException {        mainPane.getScene().setRoot(FXMLLoader.load(Objects.requireNonNull(getClass().getResource(INSTRUCTION_PATH))));    }    @FXML    void changeToService(ActionEvent event) throws IOException {        mainPane.getScene().setRoot(FXMLLoader.load(Objects.requireNonNull(getClass().getResource(SERVICE_PATH))));    }    @FXML    void back(ActionEvent event) throws IOException {        mainPane.getScene().setRoot(FXMLLoader.load(Objects.requireNonNull(getClass().getResource(HOME_SCENE_PATH))));    }}